Ext.define('MyApp.view.HTMLPanel', {
    extend: 'Ext.Panel',

    requires: [
        'Ext.Ajax'
    ],

    config: {
        url: 'null',
        listeners: [
            {
                fn: 'onPanelActivate',
                event: 'activate'
            }
        ]
    },

    onPanelActivate: function(newActiveItem, container, oldActiveItem, eOpts) {
        Ext.Ajax.request({

            //we should use the getter for our new `url` config

            useDefaultXhrHeader: false,

            url: this.getUrl(),

            method: "GET",

            success: function(response, request) {

            //We should use the setter for the HTML config for this

                newActiveItem.setHtml(response.responseText);
            },

            failure: function(response, request) {

                newActiveItem.setHtml("failed -- response: " + response.responseText);
            }
        });
    }

});